% KRETTANGOLO(E,t,v,a,b) restituisce la matrice di rigidezza di un elemento
% finito rettangolare.
% -E [MPa] = modulo di Young dell'elemento rettangolare.
% -t [mm] = spessore dell'elemento finito.
% -v = modulo di Poisson dell'elemento finito.
% -a [mm] = lunghezza della base dell'elemento finito.
% -b [mm] = lunghezza dell'altezza dell'elemento finito.
%
% 
% Autori: Fuso Andrea, Gaeta Gianpiero
% Versione: 1.0
function [k_rettangolo] = kRettangolo(E,t,v,a,b)
k_rettangolo= [ -(E*t*(2*a^2 - b^2*v + b^2))/(6*a*b*(v^2 - 1)) (E*t*(b^2*v + 4*a^2 - b^2))/(12*a*b*(v^2 - 1)) (E*t*(2*a^2 - b^2*v + b^2))/(12*a*b*(v^2 - 1)) -(E*t*(b^2*v + a^2 - b^2))/(6*a*b*(v^2 - 1)) -(E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1));...
 (E*t*(b^2*v + 4*a^2 - b^2))/(12*a*b*(v^2 - 1)) -(E*t*(2*a^2 - b^2*v + b^2))/(6*a*b*(v^2 - 1)) -(E*t*(b^2*v + a^2 - b^2))/(6*a*b*(v^2 - 1)) (E*t*(2*a^2 - b^2*v + b^2))/(12*a*b*(v^2 - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1));...
 (E*t*(2*a^2 - b^2*v + b^2))/(12*a*b*(v^2 - 1)) -(E*t*(b^2*v + a^2 - b^2))/(6*a*b*(v^2 - 1)) -(E*t*(2*a^2 - b^2*v + b^2))/(6*a*b*(v^2 - 1)) (E*t*(b^2*v + 4*a^2 - b^2))/(12*a*b*(v^2 - 1)) (E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1));...
   -(E*t*(b^2*v + a^2 - b^2))/(6*a*b*(v^2 - 1)) (E*t*(2*a^2 - b^2*v + b^2))/(12*a*b*(v^2 - 1)) (E*t*(b^2*v + 4*a^2 - b^2))/(12*a*b*(v^2 - 1)) -(E*t*(2*a^2 - b^2*v + b^2))/(6*a*b*(v^2 - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1));...
 -(E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t*(a^2 - a^2*v + 2*b^2))/(6*a*b*(v^2 - 1)) -(E*t*(a^2*v - a^2 + b^2))/(6*a*b*(v^2 - 1)) (E*t*(a^2 - a^2*v + 2*b^2))/(12*a*b*(v^2 - 1)) (E*t*(a^2*v - a^2 + 4*b^2))/(12*a*b*(v^2 - 1));...
 -(E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1)) -(E*t*(a^2*v - a^2 + b^2))/(6*a*b*(v^2 - 1)) -(E*t*(a^2 - a^2*v + 2*b^2))/(6*a*b*(v^2 - 1)) (E*t*(a^2*v - a^2 + 4*b^2))/(12*a*b*(v^2 - 1)) (E*t*(a^2 - a^2*v + 2*b^2))/(12*a*b*(v^2 - 1));...
 (E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1)) (E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t*(a^2 - a^2*v + 2*b^2))/(12*a*b*(v^2 - 1)) (E*t*(a^2*v - a^2 + 4*b^2))/(12*a*b*(v^2 - 1)) -(E*t*(a^2 - a^2*v + 2*b^2))/(6*a*b*(v^2 - 1)) -(E*t*(a^2*v - a^2 + b^2))/(6*a*b*(v^2 - 1));...
 (E*t*(3*v - 1))/(8*(v^2 - 1)) -(E*t)/(8*(v - 1)) -(E*t*(3*v - 1))/(8*(v^2 - 1)) (E*t)/(8*(v - 1)) (E*t*(a^2*v - a^2 + 4*b^2))/(12*a*b*(v^2 - 1)) (E*t*(a^2 - a^2*v + 2*b^2))/(12*a*b*(v^2 - 1)) -(E*t*(a^2*v - a^2 + b^2))/(6*a*b*(v^2 - 1)) -(E*t*(a^2 - a^2*v + 2*b^2))/(6*a*b*(v^2 - 1))];
